html:
{% set qty = calculate_qty(doc.workorder_doc.qty if doc.workorder_doc else 0, doc.item_doc.custom_unit_pack) %}
{% set card_number = 8 %}
{% set customer_name = doc.custom_customer_name or "-" %}
{% set item_name = doc.item_name or "-" %}
{% set customer_part_no = doc.customer_item and doc.customer_item.ref_code or "-" %}
{% set unit_pack = frappe.format(doc.item_doc.custom_unit_pack or 0) %}
{% set quantity = unit_pack  ~ " " ~ doc.item_doc.stock_uom %}
{% set batch_no = doc.name or "-" %}
{% set barcode_data_1 = customer_part_no %}
{% set barcode_data_2 = unit_pack ~ " / " ~ batch_no %}
{% set mfg_date = doc.manufacturing_date and frappe.utils.getdate(doc.manufacturing_date).strftime("%d/%m/%Y") or "-" %}
{% set exp_date = doc.expiry_date and frappe.utils.getdate(doc.expiry_date).strftime("%d/%m/%Y") or "-" %}
{% set qr_code_data = item_name ~ " / " ~ customer_part_no ~ " / " ~ unit_pack ~ " / " ~ batch_no ~ " / " ~ mfg_date ~ " / " ~ exp_date %}
{% set company_name = "LAMPHUN PLASTPACK CO., LTD." %}
{% set country_of_origin = "Made in Thailand" %}
{% set storage_instructions = "Please keep away from sunlight and store at a temperature not exceeding 40°C" %}
{{ frappe.render_template("templates/jinja_lpp_label_2.html", {
    "qty": qty,
    "card_number": card_number,
    "customer_name": customer_name,
    "item_name": item_name,
    "customer_part_no": customer_part_no,
    "barcode_data_1": barcode_data_1,
    "barcode_data_2": barcode_data_2,
    "quantity": quantity,
    "batch_no": batch_no,
    "mfg_date": mfg_date,
    "exp_date": exp_date,
    "qr_code_data": qr_code_data,
    "company_name": company_name,
    "country_of_origin": country_of_origin,
    "storage_instructions": storage_instructions
    })
}}
