html:
{% set qty = calculate_qty(doc.workorder_doc.qty if doc.workorder_doc else 0, doc.item_doc.custom_unit_box) %}
{% set card_number = 8 %}
{% set customer_name = doc.custom_customer_name or "-" %}
{% set item_name = doc.item_name or "-" %}
{% set customer_part_no = doc.customer_item and doc.customer_item.ref_code or "-" %}
{% set barcode_data_1 = customer_part_no %}
{% set batch_no = doc.name or "-" %}
{% set barcode_data_2 = batch_no %}
{% set unit_box = frappe.format(doc.item_doc.custom_unit_box or 0) %}
{% set quantity = unit_box ~ " Reels" %}
{% set barcode_data_3 = unit_box ~ "" %}
{% set exp_date = doc.expiry_date and frappe.utils.getdate(doc.expiry_date).strftime("%d/%m/%Y") or "-" %}
{% set barcode_data_4 = exp_date %}
{% set company = "LPP" %}
{% set manufacturing_address = "TH" %}
{% set mfg_date = doc.manufacturing_date and frappe.utils.getdate(doc.manufacturing_date).strftime("%d/%m/%Y") or "-" %}
{% set qr_code_data = "B04^^" ~ customer_part_no ~ "^" ~ batch_no ~ "^" ~ unit_box ~ "^" ~ "Reels" ~ "^^" ~ exp_date ~ "^^"  %}
{% set storage_conditions = "23-40°C, ≤50%RH" %}
{{ frappe.render_template("templates/jinja_lpp_label_11.html", {
    "qty": qty,
    "card_number": card_number,
    "customer_name": customer_name,
    "item_name": item_name,
    "customer_part_no": customer_part_no,
    "barcode_data_1": barcode_data_1,
    "batch_no": batch_no,
    "barcode_data_2": barcode_data_2,
    "quantity": quantity,
    "barcode_data_3": barcode_data_3,
    "exp_date": exp_date,
    "barcode_data_4": barcode_data_4,
    "company": company,
    "manufacturing_address": manufacturing_address,
    "mfg_date": mfg_date,
    "qr_code_data": qr_code_data,
    "storage_conditions": storage_conditions,
    })
}}
