html:
{% if doc.reference_doctype == "Work Order" and doc.reference_name %}
    {% set wo = frappe.db.get_value(
        "Work Order",
        doc.reference_name,
        ["qty", "production_item", "custom_customer_name"],
        as_dict=True)
    %}
    {% set item = frappe.db.get_value(
        "Item",
        wo.production_item,
        ["item_name", "custom_unit_box", "stock_uom"],
        as_dict=True)
    %}
    {% set item_details = frappe.db.get_all(
        "Item Customer Detail",
        {"parenttype": "Item", "parentfield": "customer_items", "parent": wo.production_item},
        ["ref_code"])
    %}
    {% set qty = calculate_qty(wo.qty, item.custom_unit_box) %}
    {% set loop_range = 0 if qty == 0 else 1 %}
    {% set customer_name = wo.custom_customer_name %}
    {% set item_name = item.item_name %}
    {% set customer_part_no = set(item_details | map(attribute="ref_code")) | reject("none") | join(", ") %}
    {% set number = "" %}
    {% set barcode_data_1 = (customer_part_no or "-") %}
    {% set barcode_data_2 = ((item.custom_unit_box | string) or "-") ~ " | " ~ (doc.name or "-") %}
    {% set quantity = frappe.format(item.custom_unit_box)  ~ " " ~ item.stock_uom %}
    {% set batch_no = doc.name %}
    {% set job_card_no = "" %}
    {% set mfg_date = doc.manufacturing_date and frappe.utils.getdate(doc.manufacturing_date).strftime("%d/%m/%Y") or "" %}
    {% set exp_date = "" %}
    {% set qr_code_data = (item_name or "-") ~ " | " ~ (customer_part_no or "-") ~ " | " ~ (quantity or "-") ~ " | " ~ (batch_no or "-") ~ " | " ~ (mfg_date or "-") %}
    {% set company_name = "" %}
    {% set country_of_origin = "Made in Thailand" %}
    {% set storage_instructions = "Please keep away from sunlight and store at a temperature not exceeding 40°C" %}
    {{ frappe.render_template("templates/jinja_lpp_label_2.html", {
        "qty": qty,
        "loop_range": loop_range,
        "customer_name": customer_name,
        "item_name": item_name,
        "customer_part_no": customer_part_no,
        "number": number,
        "barcode_data_1": barcode_data_1,
        "barcode_data_2": barcode_data_2,
        "quantity": quantity,
        "batch_no": batch_no,
        "job_card_no": job_card_no,
        "mfg_date": mfg_date,
        "exp_date": exp_date,
        "qr_code_data": qr_code_data,
        "company_name": company_name,
        "country_of_origin": country_of_origin,
        "storage_instructions": storage_instructions
        })
    }}
{% endif %}
