css:
body {
    padding: 0mm 0 5mm !important;
}
.print-format {
    line-height: 1.5;
    margin-left: 5mm;
    margin-right: 5mm;
}
.print-format, .print-format td, .print-format th {
    font-size: 12px !important;
    color: #000000 !important;
}
.print-format-preview{
    padding: 5mm;
}
.logo {
    margin-top: 10px;
    width: 100px;
}
.title {
    font-size: 18px;
}
table {
    width: 100%;
}
.table-head {
    border: 1px solid #000000;
}
.table-detail {
    border-bottom: 1px solid #d3d3d3;
}
.col-1 {
    width: 5%;
}
.col-2 {
    width: 35%;
}
.col-3, .col-4, .col-5, .col-6 {
    width: 15%;
}
.col-foot-1 {
    width: 55%;
}
.col-foot-2 {
    width: 30%;
}
.col-foot-3 {
    width: 15%;
}
.page-break-inside {
    page-break-inside: avoid;
}
.add-border{
    border: 1px solid black !important;
}
.color-thin{
    color: #6D6E71;
}
