css:
.print-format {
    margin-left: 10mm;
    margin-right: 10mm;
    font-size: 16px;
    line-height: 1.8;
    font-family: "NotoSansThaiLooped";
}
.title {
    font-size: 20px;
    border: 1px solid;
}
.item {
    border: 1px solid;
}
.small-text {
    font-size: 9px;
}
.ql-editor {
    line-height: 1 !important;
    overflow: hidden !important;
}
.square {
    width: 20px;
    height: 20px;
    padding: 5px;
    margin-top: 1px;
    border: 1px solid;
}
